import React, { useState, useEffect } from 'react';
import CustomSelect from '../components/CustomSelect';
import CalculationResultsView from '../components/CalculationResultsView';
import DepartmentComparisonView from '../components/DepartmentComparisonView';
import { departmentsAPI } from '../utils/api';
import '../styles/analytics.css';

const Analytics = () => {
  const [activeTab, setActiveTab] = useState('results');
  const [subdivision, setSubdivision] = useState('');
  const [subA, setSubA] = useState('');
  const [subB, setSubB] = useState('');
  const [subdivisions, setSubdivisions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchSubdivisions = async () => {
      try {
        const response = await departmentsAPI.getAll();
        setSubdivisions(response.data);
        if (response.data.length > 0) {
          const firstId = response.data[0].id;
          setSubdivision(firstId);
          setSubA(firstId);
          setSubB(firstId);
        }
      } catch (error) {
        console.error("Ошибка загрузки подразделений", error);
      } finally {
        setLoading(false);
      }
    };

    fetchSubdivisions();
  }, []);

  if (loading) return <div>Загрузка...</div>;

  const tabs = [
    { id: 'results', label: 'Результаты расчетов', component: <CalculationResultsView subdivision={subdivision} /> },
    { id: 'comparison', label: 'Сравнение подразделений', component: <DepartmentComparisonView subA={subA} subB={subB} /> },
  ];

  return (
    <div className="analytics-container">
      <h2>Аналитика</h2>

      <div className="tabs">
        {tabs.map(tab => (
          <button
            key={tab.id}
            className={`tab ${activeTab === tab.id ? 'active' : ''}`}
            onClick={() => setActiveTab(tab.id)}
          >
            {tab.label}
          </button>
        ))}
      </div>

      <div className="tab-content">
        {tabs.find(t => t.id === activeTab)?.component}
      </div>
    </div>
  );
};

export default Analytics;
