import React, { useState, useEffect } from 'react';
import { calculationsAPI, departmentsAPI } from '../utils/api';
import '../styles/comparison-chart.css';

const getDefaultResult = () => ({
  actual_hours: 0,
  required_hours: 0,
  deficit_count: 0,
  efficiency_coefficient: 0,
  utilization_rate: 0
});

const ComparisonChart = ({ subA, subB, chartType }) => {
  const [dataA, setDataA] = useState(null);
  const [dataB, setDataB] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (subA && subB) {
      fetchComparisonData();
    }
  }, [subA, subB, chartType]);

  const fetchComparisonData = async () => {
    setLoading(true);
    setError('');
    try {
      const [deptARes, deptBRes, resultARes, resultBRes] = await Promise.allSettled([
        departmentsAPI.get(subA),
        departmentsAPI.get(subB),
        calculationsAPI.getResults(subA),
        calculationsAPI.getResults(subB)
      ]);

      if (deptARes.status === 'rejected' || deptBRes.status === 'rejected') {
        throw new Error('Не удалось загрузить данные подразделений');
      }

      const dataA = {
        name: deptARes.value.data.name,
        ... (resultARes.status === 'fulfilled' ? resultARes.value.data : getDefaultResult())
      };
      const dataB = {
        name: deptBRes.value.data.name,
        ... (resultBRes.status === 'fulfilled' ? resultBRes.value.data : getDefaultResult())
      };

      setDataA(dataA);
      setDataB(dataB);
    } catch (err) {
      console.error("Ошибка загрузки данных для сравнения", err);
      setError("Не удалось загрузить данные для сравнения");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Загрузка данных для сравнения...</div>;
  if (error) return <div className="error-message">{error}</div>;
  if (!dataA || !dataB) return <div>Нет данных для сравнения</div>;

  const getValue = (data) => {
    switch (chartType) {
      case 'hours': return data.actual_hours;
      case 'deficit': return data.deficit_count;
      case 'efficiency': return data.efficiency_coefficient;
      case 'utilization': return data.utilization_rate || (data.required_hours ? (data.actual_hours / data.required_hours) * 100 : 0);
      default: return 0;
    }
  };

  const valueA = getValue(dataA);
  const valueB = getValue(dataB);
  const labelA = dataA.name;
  const labelB = dataB.name;
  const maxValue = Math.max(Math.abs(valueA), Math.abs(valueB)) * 1.2;

  const getChartTitle = () => {
    switch (chartType) {
      case 'hours': return 'Фонд времени (часы)';
      case 'deficit': return 'Дефицит/избыток персонала';
      case 'efficiency': return 'Общая эффективность';
      case 'utilization': return 'Загрузка персонала (%)';
      default: return 'Сравнение';
    }
  };

  const getUnit = () => {
    switch (chartType) {
      case 'hours': return 'часов';
      case 'deficit': return 'чел.';
      case 'efficiency': return '';
      case 'utilization': return '%';
      default: return '';
    }
  };

  return (
    <div className="comparison-chart">
      <h3>{getChartTitle()}</h3>
      <div className="chart-container">
        <div className="y-axis">
          {[0, 25, 50, 75, 100].map((percent, i) => (
            <div key={i} className="y-tick">
              <span className="y-label">
                {Math.round(maxValue * (percent / 100))}
              </span>
            </div>
          ))}
        </div>
        <div className="bars">
          <div className="bar-group">
            <div 
              className="bar-line bar-line-a" 
              style={{ height: `${Math.abs(valueA) / maxValue * 100}%` }}
            ></div>
            <div 
              className="bar bar-a" 
              style={{ height: `${Math.abs(valueA) / maxValue * 100}%` }}
            ></div>
            <div className="bar-label">{labelA}</div>
            <div className="bar-value">{valueA.toFixed(2)} {getUnit()}</div>
          </div>
          <div className="bar-group">
            <div 
              className="bar-line bar-line-b" 
              style={{ height: `${Math.abs(valueB) / maxValue * 100}%` }}
            ></div>
            <div 
              className="bar bar-b" 
              style={{ height: `${Math.abs(valueB) / maxValue * 100}%` }}
            ></div>
            <div className="bar-label">{labelB}</div>
            <div className="bar-value">{valueB.toFixed(2)} {getUnit()}</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ComparisonChart;
