import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { departmentsAPI } from './utils/api';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Calculation from './pages/Calculation';
import Analytics from './pages/Analytics';
import Subdivisions from './pages/Subdivisions';
import Loader from './components/Loader';
import './App.css';

function App() {
  const [loading, setLoading] = useState(true);
  const [hasDepartments, setHasDepartments] = useState(false);

  useEffect(() => {
    const checkDepartments = async () => {
      try {
        const response = await departmentsAPI.getStatus();
        setHasDepartments(response.data.has_departments);
      } catch (error) {
        console.error("Ошибка проверки подразделений", error);
      } finally {
        setLoading(false);
      }
    };

    checkDepartments();
  }, []);

  if (loading) return <Loader />;

  return (
    <Router>
      <div className="app">
        <Header />
        <main>
          <Routes>
            <Route path="/" element={hasDepartments ? <Home /> : <Navigate to="/subdivisions" />} />
            <Route path="/calculation" element={hasDepartments ? <Calculation /> : <Navigate to="/subdivisions" />} />
            <Route path="/analytics" element={hasDepartments ? <Analytics /> : <Navigate to="/subdivisions" />} />
            <Route path="/subdivisions" element={<Subdivisions />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
