import React, { useState } from 'react';
import '../styles/calculation.css';

const Calculation = () => {
  const [activeTab, setActiveTab] = useState('staff');

  const tabs = [
    { id: 'staff', label: 'Личный состав' },
    { id: 'equipment', label: 'Оснащение' },
    { id: 'supervision', label: 'Объекты надзора' },
    { id: 'planning', label: 'Планирование' },
  ];

  return (
    <div className="calculation-container">
      <h2>Калькулятор эффективности</h2>

      <div className="tabs">
        {tabs.map(tab => (
          <button
            key={tab.id}
            className={`tab ${activeTab === tab.id ? 'active' : ''}`}
            onClick={() => setActiveTab(tab.id)}
          >
            {tab.label}
          </button>
        ))}
      </div>

      <div className="tab-content">
        {activeTab === 'staff' && (
          <div>
            <h3>Личный состав</h3>
            <label>Количество сотрудников</label>
            <input type="number" />
            <label>Средний уровень подготовки (%)</label>
            <input type="number" />
          </div>
        )}
        {activeTab === 'equipment' && (
          <div>
            <h3>Оснащение</h3>
            <label>Количество техники</label>
            <input type="number" />
            <label>Средний износ (%)</label>
            <input type="number" />
          </div>
        )}
        {activeTab === 'supervision' && (
          <div>
            <h3>Объекты надзора</h3>
            <label>Количество объектов</label>
            <input type="number" />
            <label>Проверено за месяц</label>
            <input type="number" />
          </div>
        )}
        {activeTab === 'planning' && (
          <div>
            <h3>Планирование</h3>
            <label>Запланировано задач</label>
            <input type="number" />
            <label>Выполнено задач</label>
            <input type="number" />
          </div>
        )}
      </div>

      <button className="primary-btn">Рассчитать</button>
    </div>
  );
};

export default Calculation;
