import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation, useNavigate } from 'react-router-dom';
import { departmentsAPI } from './utils/api';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Calculation from './pages/Calculation';
import Analytics from './pages/Analytics';
import Subdivisions from './pages/Subdivisions';
import Loader from './components/Loader';
import './App.css';

// Компонент для проверки статуса подразделений перед доступом к "Расчетам" и "Аналитике"
const ProtectedRoute = ({ children }) => {
  const [loading, setLoading] = useState(true);
  const [hasDepartments, setHasDepartments] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const checkDepartments = async () => {
      try {
        const response = await departmentsAPI.getStatus();
        // console.log("Статус подразделений:", response.data); // Для отладки
        setHasDepartments(response.data.has_departments);
      } catch (error) {
        console.error("Ошибка проверки подразделений", error);
        // В случае ошибки API, можно разрешить доступ, чтобы не блокировать пользователя
        setHasDepartments(true);
      } finally {
        setLoading(false);
      }
    };

    // Проверяем статус при заходе на защищенные страницы
    if (location.pathname === '/calculation' || location.pathname === '/analytics') {
      checkDepartments();
    } else {
      setLoading(false);
    }
  }, [location]); // Зависимость от location, чтобы перепроверять при смене страницы

  // Проверяем при каждом рендере, если находимся на нужной странице
  if (location.pathname === '/calculation' || location.pathname === '/analytics') {
    if (loading) {
      return <Loader />;
    }

    if (!hasDepartments) {
      // console.log("Нет подразделений, показываем alert и перенаправляем"); // Для отладки
      alert("Для выполнения расчетов необходимо сначала создать подразделение.");
      navigate('/subdivisions');
      return null;
    }

    // Если подразделения есть, рендерим children
    return children;
  }

  // Если не на защищенной странице, просто рендерим children
  return children;
};

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Можно убрать проверку при старте, если не нужна
    setLoading(false);
  }, []);

  if (loading) return <Loader />;

  return (
    <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
      <div className="app">
        <Header />
        <main>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/calculation" element={
              <ProtectedRoute>
                <Calculation />
              </ProtectedRoute>
            } />
            <Route path="/analytics" element={
              <ProtectedRoute>
                <Analytics />
              </ProtectedRoute>
            } />
            <Route path="/subdivisions" element={<Subdivisions />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
