import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation, useNavigate } from 'react-router-dom';
import { departmentsAPI } from './utils/api';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import Calculation from './pages/Calculation';
import Analytics from './pages/Analytics';
import Subdivisions from './pages/Subdivisions';
import Loader from './components/Loader';
import './App.css';

// Компонент для проверки статуса подразделений перед доступом к "Расчетам"
const ProtectedRoute = ({ children }) => {
  const [loading, setLoading] = useState(true);
  const [hasDepartments, setHasDepartments] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const checkDepartments = async () => {
      try {
        const response = await departmentsAPI.getStatus();
        setHasDepartments(response.data.has_departments);
      } catch (error) {
        console.error("Ошибка проверки подразделений", error);
        // В случае ошибки API, можно разрешить доступ, чтобы не блокировать пользователя
        setHasDepartments(true);
      } finally {
        setLoading(false);
      }
    };

    // Проверяем только при заходе на /calculation
    if (location.pathname === '/calculation') {
      checkDepartments();
    } else {
      setLoading(false);
    }
  }, [location]);

  if (loading && location.pathname === '/calculation') {
    return <Loader />;
  }

  if (location.pathname === '/calculation' && !hasDepartments) {
    alert("Для выполнения расчетов необходимо сначала создать подразделение.");
    navigate('/subdivisions');
    return null;
  }

  return children;
};

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Можно убрать проверку при старте, если не нужна
    setLoading(false);
  }, []);

  if (loading) return <Loader />;

  return (
    <Router>
      <div className="app">
        <Header />
        <main>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/calculation" element={
              <ProtectedRoute>
                <Calculation />
              </ProtectedRoute>
            } />
            <Route path="/analytics" element={
              <ProtectedRoute>
                <Analytics />
              </ProtectedRoute>
            } />
            <Route path="/subdivisions" element={<Subdivisions />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
