import React, { useState } from 'react';
import '../styles/subdivisions.css';

const Subdivisions = () => {
  const [subdivisions, setSubdivisions] = useState([
    { id: 1, name: 'ПСЧ-12', leader: 'Иванов И.И.', count: 25, tech: 4, status: 'active' },
    { id: 2, name: 'ПСЧ-15', leader: 'Петров П.П.', count: 18, tech: 3, status: 'critical' },
    { id: 3, name: 'ПСЧ-20', leader: 'Сидоров С.С.', count: 30, tech: 6, status: 'warning' },
    { id: 4, name: 'ПСЧ-25', leader: 'Козлов К.К.', count: 0, tech: 0, status: 'none' },
  ]);

  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({ name: '', leader: '', count: '', tech: '' });

  const handleAdd = () => setShowModal(true);
  const handleSave = () => {
    if (formData.name && formData.leader) {
      setSubdivisions([
        ...subdivisions,
        {
          id: subdivisions.length + 1,
          name: formData.name,
          leader: formData.leader,
          count: parseInt(formData.count) || 0,
          tech: parseInt(formData.tech) || 0,
          status: 'active',
        }
      ]);
    }
    setShowModal(false);
    setFormData({ name: '', leader: '', count: '', tech: '' });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const getStatusInfo = (status) => {
    switch (status) {
      case 'active': return 'Активно';
      case 'critical': return 'Критическое состояние';
      case 'warning': return 'Предупреждение';
      case 'none': return 'Нет данных';
      default: return 'Неизвестно';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return 'green';
      case 'critical': return 'red';
      case 'warning': return 'yellow';
      case 'none': return 'white';
      default: return 'gray';
    }
  };

  return (
    <div className="subdivisions-container">
      <h2>Управление подразделениями</h2>
      <button onClick={handleAdd} className="primary-btn">Добавить подразделение</button>

      <table className="subdivisions-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Название</th>
            <th>Руководитель</th>
            <th>Количество</th>
            <th>Техника</th>
            <th>Статус</th>
            <th>Действия</th>
          </tr>
        </thead>
        <tbody>
          {subdivisions.map(sub => (
            <tr key={sub.id}>
              <td>{sub.id}</td>
              <td>{sub.name}</td>
              <td>{sub.leader}</td>
              <td>{sub.count}</td>
              <td>{sub.tech}</td>
              <td>
                <div className="status-indicator-container">
                  <div
                    className="status-indicator"
                    style={{ backgroundColor: getStatusColor(sub.status) }}
                    title={getStatusInfo(sub.status)}
                  ></div>
                </div>
              </td>
              <td>
                <button>Редактировать</button>
                <button>Удалить</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Добавить подразделение</h3>
            <input name="name" placeholder="Название" value={formData.name} onChange={handleChange} />
            <input name="leader" placeholder="Руководитель" value={formData.leader} onChange={handleChange} />
            <input name="count" placeholder="Количество" value={formData.count} onChange={handleChange} />
            <input name="tech" placeholder="Техника" value={formData.tech} onChange={handleChange} />
            <div>
              <button onClick={handleSave}>Сохранить</button>
              <button onClick={() => setShowModal(false)}>Отмена</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Subdivisions;
